import { MDBContainer, MDBRow, MDBCol, MDBCard, MDBCardBody, MDBInput, MDBBtn, MDBIcon } from 'mdbreact';
import { Redirect } from 'react-router-dom';
import React, { Component } from 'react';
import LoggedIn from './LoggedIn'

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {password: ''};
    this.state = {email: ''};
    this.state = {toLogin: false};
    this.handlePasswordChange = this.handlePasswordChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleEmailChange = this.handleEmailChange.bind(this);
  }


  //the logic after the fields are entered
  handleSubmit(){
    let Email = this.state.email;
    let Password= this.state.password;
    if(Email == "test@test.com"&& Password == "123456"){
      console.log("in")
      this.setState({toLogin: true});


    }else{
      console.log("try again")
    }

  }
  // handles any change in password
  handlePasswordChange(event) {
    this.setState({password: event.target.value});
  }
  // handels any change in the email 
  handleEmailChange(event) {
    this.setState({email: event.target.value});
  }
  render(){
    if(this.state.toLogin== true){
      <LoggedIn  Name= "Mark" email={this.state.email}/>
    }
    return (
      <MDBRow>
        <MDBRow>
          <MDBCol md="6">
            <MDBCard>
              <div className="header pt-3 peach-gradient">
                <MDBRow className="d-flex justify-content-center">
                  <h3 className="blue-text mb-3 pt-3 font-weight-bold">
                    Log in
                  </h3>
                </MDBRow>
              </div>
              <MDBCardBody className="mx-4 mt-4">
                <MDBInput 
                onChange={this.handleEmailChange}
                label="Your email"
                group
                type="email"
                validate
                error="wrong"
                success="right"
                />
                <MDBInput
                  onChange={this.handlePasswordChange}
                  value={this.state.password}
                  label="Your password"
                  group
                  type="password"
                  validate = "true"
                  containerClass="mb-0"
                />
                <p className="font-small grey-text d-flex justify-content-end">
                  Forgot
                  <a
                    href="#!"
                    className="dark-grey-text ml-1 font-weight-bold"
                  >
                    Password?
                  </a>
                </p>
                <MDBRow >
                    <div className="text-center">
                      <button className="waves-effect waves-light btn blue "
                        onClick={()=>{this.handleSubmit()}}
                        >
                        Log in
                      </button>

                    </div>
                </MDBRow>
              </MDBCardBody>
            </MDBCard>
          </MDBCol>
        </MDBRow>
      </MDBRow>
    );
  };
  }


export default Login;
