import { connect } from 'react-redux'
import { Link } from 'react-router-dom'
import './Navbar.css'
import React, { Component } from 'react';

class Navbar extends Component {

    render() {
        let itemList = this.props.items;
        console.log(itemList);
        return(
            <div className="NavBar ">
            <nav class="navbar navbar-expand-lg navbar-light px-5 gb-white">
            <Link class="navbar-brand" to="/" style={{fontSize : "40px"}}>{this.props.CompanyName}</Link>
            <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <span class="navbar-toggler-icon"></span>
            </button>
            <div class="collapse navbar-collapse " id="navbarSupportedContent">
            <ul class="navbar-nav ml-auto">
            <li class="nav-item">
            <Link to="/menu" style={{fontSize : "20px"}} class= "nav-link">Menu</Link>
            </li>
            <li class="nav-item">
              <a class="nav-link" href="#" style={{fontSize : "20px"}}>Blog</a>
            </li>
            <li class="nav-item">
              <a class="nav-link" href="#" style={{fontSize : "20px"}}>Login</a>
            </li>
            <li class="nav-item">
            <Link to="/cart">
            <div id ='test'>
            <span id="numberOfItemsAdded">{itemList.length}</span>
            <i class="fas fa-box-open shoppingChart" style={{fontSize : "20px"}}></i>
            </div>
            </Link>
            </li>
            
          </ul>
            </div>
            </nav>
            </div>
                
       
            
        )
    }
}

const mapStateToProps = (state)=>{
    return{
        items: state.addedItems,
        //addedItems: state.addedItems
    }
}

export default connect(mapStateToProps)(Navbar)
